package met.tns.operator;

public class AssignmentOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x= 12;
		int y=11;
		System.out.println("Value of x will be"+(x+=y));
		System.out.println("Value of x will be"+(x*=y));
		System.out.println("Value of x will be"+(x/=y));

	}

}
